insert into bbs_article_slider ( slider_id, slider_title, poster_little, poster, slider_url, slider_type, sort, create_user, create_time, update_user, update_time, flag ) values ( #{sliderId,jdbcType=VARCHAR}, #{sliderTitle,jdbcType=VARCHAR}, #{posterLittle,jdbcType=VARCHAR}, #{poster,jdbcType=VARCHAR}, #{sliderUrl,jdbcType=VARCHAR}, #{sliderType,jdbcType=TINYINT}, #{sort,jdbcType=INTEGER}, #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{flag,jdbcType=TINYINT}
